mapper.select:
SELECT PAY_NUM, PAY_DATE, PAY_PRICE, PAY_STEP, MEMBER_ID, LECTURE_NUM, LECTURE_NAME, LECTURE_CATEGORY
FROM PAY
WHERE MEMBER_ID = #{member_id}

SELECT PAY_NUM, PAY_DATE, PAY_PRICE, PAY_STEP, MEMBER_ID, LECTURE_NUM, LECTURE_NAME, LECTURE_CATEGORY
FROM PAY
WHERE PAY_NUM = #{pay_num}

mapper.update:
UPDATE PAY
SET PAY_DATE = SYSDATE, PAY_STEP = #{pay_step}
WHERE PAY_NUM = #{pay_num}
