mapper.select:
INSERT INTO BOARDREPLY
VALUES(
REPLY_NO_SEQ.NEXTVAL, REPLY_GROUPNO_SEQ.NEXTVAL, 1, 0, 'N', #{reply_content}, SYSDATE, #{br_num}, #{reply_member_id}
)

mapper.update:
UPDATE BOARDREPLY
SET REPLY_CONTENT = #{reply_content}
WHERE REPLY_NO = #{reply_no}

UPDATE BOARDREPLY
SET REPLY_DELFLAG = 'Y'
WHERE REPLY_NO = #{reply_no}
